fun jsStyleBase64Decode(input: String): ByteArray {
    // Same regexp as JS: e.replace(/[^A-Za-z0-9\+\/\=]/g,"")
    val sanitized = input.replace(Regex("[^A-Za-z0-9+/=]"), "")
    return Base64.getDecoder().decode(sanitized)
}


fun ddd() {

}

fun jsStyleBase64DecodeToString(input: String): String {
    val bytes = jsStyleBase64Decode(input)
    // JS side uses its own _utf8_decode, which is just normal UTF-8.
    return bytes.toString(Charsets.UTF_8)
}


@SuppressLint("NewApi")
fun extractPlayerFileFromHtml(html: String): String? {
    val doc = Jsoup.parse(html)

    val div = doc.selectFirst("#xTyBxQyGTA") ?: return null
    val base64 = div.text().trim()
//    println(base64)
    if (base64.isEmpty()) return null

    val compressedBytes = Base64.getDecoder().decode(base64)
    val decodedText = jsStyleBase64DecodeToString(base64)
    println(compressedBytes)
    println(decodedText)
    return ""
}

fun main(args: Array<String>) {
    val html = """
           <div id="xTyBxQyGTA" style="display:none;"></div>
    """.trimIndent()

    val playerFile = extractPlayerFileFromHtml(html)

    if (playerFile != null) {
        println("Player file URL = $playerFile")
    }

}